import React from 'react';
import { motion } from 'framer-motion';
import { FaExternalLinkAlt, FaGithub, FaQuoteLeft, FaEnvelope, FaLinkedin, FaReact, FaJs, FaCss3Alt, FaHtml5 } from 'react-icons/fa';

// Animation variant for fade and slide effect
const fadeSlide = {
  hidden: { opacity: 0, y: 40 },
  visible: { opacity: 1, y: 0, transition: { duration: 0.8 } }
};

// Card animation for projects
const cardVariants = {
  hidden: { opacity: 0, y: 40 },
  visible: { opacity: 1, y: 0, transition: { duration: 0.7 } },
  hover: { scale: 1.03 }
};

// Example tech stack data
const techStack = [
  { name: 'React', icon: <FaReact color="#61dafb" /> },
  { name: 'JavaScript', icon: <FaJs color="#f7df1e" /> },
  { name: 'CSS3', icon: <FaCss3Alt color="#2965f1" /> },
  { name: 'HTML5', icon: <FaHtml5 color="#e34c26" /> },
];

// Example projects data
const projects = [
  {
    title: 'Modern Portfolio',
    description: 'A beautiful, animated portfolio site built with React and Framer Motion.',
    image: 'https://images.unsplash.com/photo-1461749280684-dccba630e2f6?auto=format&fit=crop&w=600&q=80',
    tech: [<FaReact />, <FaJs />, <FaCss3Alt />],
    live: 'https://yourportfolio.com',
    github: 'https://github.com/yourusername/portfolio',
  },
  {
    title: 'E-commerce Store',
    description: 'A full-featured e-commerce app with cart, checkout, and admin dashboard.',
    image: 'https://images.unsplash.com/photo-1519125323398-675f0ddb6308?auto=format&fit=crop&w=600&q=80',
    tech: [<FaReact />, <FaJs />],
    live: 'https://yourecommerce.com',
    github: 'https://github.com/yourusername/ecommerce',
  },
];

// Example testimonials data
const testimonials = [
  {
    quote: 'Alex delivered a fantastic website, on time and beyond expectations!',
    image: 'https://randomuser.me/api/portraits/women/44.jpg',
    name: 'Jane Doe',
    title: 'Product Manager, Acme Inc.'
  },
  {
    quote: 'Great communication and technical skills. Highly recommended!',
    image: 'https://randomuser.me/api/portraits/men/32.jpg',
    name: 'John Smith',
    title: 'CTO, BetaTech'
  }
];

// Example experience data
const experience = [
  {
    role: 'Amazon SDE Intern',
    company: 'Amazon',
    period: 'May–Aug 2025',
    description: 'Built AWS CDK constructs and designed automation (see resume for details).'
  },
  {
    role: 'UI/UX Coordinator',
    company: 'ASU',
    period: '2024 - 2025',
    description: 'Coordinated UI/UX for student projects and led design sprints.'
  }
];

const Home = () => {
  return (
    <div className="portfolio-root">
      {/* Hero Section */}
      <motion.section id="hero" className="hero-section" initial="hidden" animate="visible" variants={fadeSlide}>
        <div className="hero-flex">
          <img src="https://randomuser.me/api/portraits/men/75.jpg" alt="Profile" className="hero-photo" />
          <div className="hero-content">
            <motion.h1 className="hero-title" initial={{ opacity: 0, y: 40 }} animate={{ opacity: 1, y: 0 }} transition={{ duration: 0.8 }}>
              Ramkumar Meenavalli
            </motion.h1>
            <motion.p className="hero-subtitle" initial={{ opacity: 0, y: 40 }} animate={{ opacity: 1, y: 0 }} transition={{ delay: 0.2, duration: 0.8 }}>
              Master of IT @ ASU | Amazon SDE Intern
            </motion.p>
            <div className="hero-socials">
              <a href="mailto:rammeenavalli0@gmail.com" className="hero-social-link"><FaEnvelope /></a>
              <a href="https://www.linkedin.com/in/ramkumar-meenavalli" target="_blank" rel="noopener noreferrer" className="hero-social-link"><FaLinkedin /></a>
              <a href="https://github.com/ramkumar122" target="_blank" rel="noopener noreferrer" className="hero-social-link"><FaGithub /></a>
            </div>
            <motion.a href="#projects" className="hero-cta" whileHover={{ scale: 1.05 }}>
              View My Work <FaExternalLinkAlt />
            </motion.a>
          </div>
        </div>
      </motion.section>

      {/* About Section */}
      <motion.section id="about" className="about-section" initial="hidden" whileInView="visible" viewport={{ once: true }} variants={fadeSlide}>
        <h2 className="about-title">About Me</h2>
        <p className="about-desc">
          Hello! I'm Ramkumar, a passionate developer focused on building beautiful, performant, and accessible web experiences. I love working with React, CSS, and modern web technologies to bring ideas to life.<br /><br />
          <strong>Education:</strong> ASU, Tempe — M.S. Information Technology (Dec 2025 expected); Anil Neerukonda Institute, India — B.Tech Information Technology (May 2023)
        </p>
        <div className="about-skills-grid">
          {techStack.map((tech, i) => (
            <div key={i} className="about-skill-item">{tech.icon}<span>{tech.name}</span></div>
          ))}
        </div>
      </motion.section>

      {/* Projects Section */}
      <motion.section id="projects" className="projects-section" initial="hidden" whileInView="visible" viewport={{ once: true }} variants={fadeSlide}>
        <h2 className="projects-title">Projects</h2>
        <div className="projects-grid">
          {projects.map((project, idx) => (
            <motion.div
              className="project-card"
              key={project.title}
              variants={cardVariants}
              initial="hidden"
              whileInView="visible"
              whileHover="hover"
              viewport={{ once: true }}
            >
              <img src={project.image} alt={project.title} className="project-image" />
              <div className="project-info">
                <h3 className="project-title">{project.title}</h3>
                <p className="project-desc">{project.description}</p>
                <div className="project-tech">
                  {project.tech.map((icon, i) => (
                    <span key={i} className="project-tech-icon">{icon}</span>
                  ))}
                </div>
                <div className="project-links">
                  <a href={project.live} target="_blank" rel="noopener noreferrer" className="project-live-link">
                    Live <FaExternalLinkAlt />
                  </a>
                  <a href={project.github} target="_blank" rel="noopener noreferrer" className="project-github-link">
                    GitHub <FaGithub />
                  </a>
                </div>
              </div>
            </motion.div>
          ))}
        </div>
      </motion.section>

      {/* Testimonials Section */}
      <motion.section id="testimonials" className="testimonials-section" initial="hidden" whileInView="visible" viewport={{ once: true }} variants={fadeSlide}>
        <h2 className="testimonials-title">Kind words from satisfied clients</h2>
        <div className="testimonials-list">
          {testimonials.map((t, i) => (
            <motion.div key={i} className="testimonial-card" initial={{ opacity: 0, y: 40 }} whileInView={{ opacity: 1, y: 0 }} viewport={{ once: true }} transition={{ duration: 0.7 }}>
              <FaQuoteLeft className="testimonial-quote-icon" />
              <p className="testimonial-quote">{t.quote}</p>
              <img src={t.image} alt={t.name} className="testimonial-photo" />
              <div className="testimonial-name">{t.name}</div>
              <div className="testimonial-title">{t.title}</div>
            </motion.div>
          ))}
        </div>
      </motion.section>

      {/* Experience Section */}
      <motion.section id="experience" className="experience-section" initial="hidden" whileInView="visible" viewport={{ once: true }} variants={fadeSlide}>
        <h2 className="experience-title">My Work Experience</h2>
        <div className="experience-list">
          {experience.map((exp, i) => (
            <motion.div key={i} className="experience-card" initial={{ opacity: 0, y: 40 }} whileInView={{ opacity: 1, y: 0 }} viewport={{ once: true }} transition={{ duration: 0.7 }}>
              <h3 className="experience-role">{exp.role} <span className="experience-company">@ {exp.company}</span></h3>
              <div className="experience-period">{exp.period}</div>
              <p className="experience-desc">{exp.description}</p>
            </motion.div>
          ))}
        </div>
      </motion.section>

      {/* Approach Section */}
      <motion.section id="approach" className="approach-section" initial="hidden" whileInView="visible" viewport={{ once: true }} variants={fadeSlide}>
        <h2 className="approach-title">My Approach</h2>
        <div className="approach-list">
          <div className="approach-card">
            <div className="approach-step">1</div>
            <h4 className="approach-step-title">Planning</h4>
            <p className="approach-step-desc">Understand client needs, define goals, and create a clear roadmap for success.</p>
          </div>
          <div className="approach-card">
            <div className="approach-step">2</div>
            <h4 className="approach-step-title">Development</h4>
            <p className="approach-step-desc">Build robust, scalable, and maintainable solutions using modern technologies.</p>
          </div>
          <div className="approach-card">
            <div className="approach-step">3</div>
            <h4 className="approach-step-title">Launch</h4>
            <p className="approach-step-desc">Deploy, monitor, and support the product for a seamless user experience.</p>
          </div>
        </div>
      </motion.section>

      {/* Contact Section */}
      <motion.section id="contact" className="contact-section" initial="hidden" whileInView="visible" viewport={{ once: true }} variants={fadeSlide}>
        <h2 className="contact-title">Contact</h2>
        <p className="contact-desc">Email: <a href="mailto:rammeenavalli0@gmail.com" className="contact-link">rammeenavalli0@gmail.com</a></p>
        <p className="contact-desc">
          <a href="https://www.linkedin.com/in/ramkumar-meenavalli" className="contact-link" target="_blank" rel="noopener noreferrer">LinkedIn</a> |
          <a href="https://github.com/ramkumar122" className="contact-link" target="_blank" rel="noopener noreferrer"> GitHub</a>
        </p>
      </motion.section>

      {/* Footer */}
      <footer className="footer-section">
        <div className="footer-socials">
          <a href="https://github.com/ramkumar122" target="_blank" rel="noopener noreferrer" className="footer-social-link"><FaGithub size={24} /></a>
          <a href="https://www.linkedin.com/in/ramkumar-meenavalli" target="_blank" rel="noopener noreferrer" className="footer-social-link"><FaLinkedin size={24} /></a>
        </div>
        <div className="footer-copy">Copyright ©️ {new Date().getFullYear()} Ramkumar Meenavalli. All rights reserved.</div>
      </footer>
    </div>
  );
};

export default Home;
