import React from 'react';
import { motion } from 'framer-motion';
import { FaEnvelope, FaLinkedin, FaGithub } from 'react-icons/fa';

const fadeSlide = {
  hidden: { opacity: 0, y: 40 },
  visible: { opacity: 1, y: 0, transition: { duration: 0.8 } }
};

export default function Connect() {
  return (
    <motion.section className="connect-section page-section" initial="hidden" animate="visible" variants={fadeSlide}>
      <div className="connect-card">
        <h2 className="connect-title">Contact</h2>
        <p className="connect-desc">I'm always open to new opportunities, collaborations, or just a friendly chat. Reach out to me via any of the platforms below!</p>
        <div className="connect-links">
          <a href="mailto:rammeenavalli0@gmail.com" className="connect-link"><FaEnvelope /> rammeenavalli0@gmail.com</a>
          <a href="https://www.linkedin.com/in/ramkumar-meenavalli" target="_blank" rel="noopener noreferrer" className="connect-link"><FaLinkedin /> LinkedIn</a>
          <a href="https://github.com/ramkumar122" target="_blank" rel="noopener noreferrer" className="connect-link"><FaGithub /> GitHub</a>
        </div>
      </div>
    </motion.section>
  );
}
