import React from 'react';
import { motion } from 'framer-motion';

const sectionVariants = {
  hidden: { opacity: 0, y: 60 },
  visible: { opacity: 1, y: 0, transition: { duration: 0.8 } }
};

const skills = [
  'Python', 'Java', 'SQL', 'TypeScript',
  'Machine Learning (scikit-learn, TensorFlow, Keras)',
  'Data Visualization (Matplotlib, Seaborn, Bokeh)',
  'Tools: Git, AWS, Jupyter'
];

const skillBadgeVariants = {
  hidden: { opacity: 0, scale: 0.8, y: 20 },
  visible: (i) => ({
    opacity: 1,
    scale: 1,
    y: 0,
    transition: { delay: i * 0.12, duration: 0.4 }
  })
};

export default function About() {
  return (
    <motion.section
      className="about-section page-section"
      initial="hidden"
      whileInView="visible"
      viewport={{ once: true, amount: 0.3 }}
      variants={sectionVariants}
    >
      <div className="about-card">
        <img src="https://randomuser.me/api/portraits/men/75.jpg" alt="Profile" className="about-photo" />
        <div className="about-main-info">
          <h2 className="about-title">About Me</h2>
          <p className="about-desc">
            Hi, I'm Ramkumar Meenavalli. I'm passionate about building modern, accessible, and performant web applications.<br /><br />
            <strong>Education:</strong><br />
            ASU, Tempe — M.S. Information Technology (Dec 2025 expected)<br />
            Anil Neerukonda Institute, India — B.Tech Information Technology (May 2023)
          </p>
          <div className="about-details-grid">
            <div>
              <h3>Experience</h3>
              <ul>
                <li><strong>Amazon SDE Intern</strong> (May–Aug 2025): Built AWS CDK constructs and designed automation.</li>
                <li><strong>UI/UX Coordinator, ASU</strong> (2024–2025): Coordinated UI/UX for student projects and led design sprints.</li>
              </ul>
            </div>
            <div>
              <h3>Skills</h3>
              <div className="about-skills-badges">
                {skills.map((skill, i) => (
                  <motion.span
                    className="about-skill-badge"
                    key={skill}
                    custom={i}
                    initial="hidden"
                    whileInView="visible"
                    viewport={{ once: true, amount: 0.6 }}
                    variants={skillBadgeVariants}
                  >
                    {skill}
                  </motion.span>
                ))}
              </div>
            </div>
          </div>
        </div>
      </div>
    </motion.section>
  );
}
