import '../styles/main.css';

import React from 'react';
import { motion } from 'framer-motion';
import { FaExternalLinkAlt, FaGithub, FaQuoteLeft, FaEnvelope, FaLinkedin, FaReact, FaJs, FaCss3Alt, FaHtml5, FaCloud } from 'react-icons/fa';

// Contact form section for Home.js
function ContactSection() {
  const [submitted, setSubmitted] = React.useState(false);
  return (
    <motion.section id="contact" className="contact-section" initial="hidden" animate="visible" variants={fadeSlide}>
      <div className="contact-card">
        <h2 className="contact-title">Contact</h2>
        <p className="contact-desc">Have a question or want to work together? Leave your details and I'll get back to you as soon as possible.</p>
        {submitted ? (
          <div className="contact-success">Thank you for your message! I'll get back to you soon.</div>
        ) : (
          <form className="contact-form" action="https://formspree.io/f/xjvnlqzq" method="POST" onSubmit={() => setSubmitted(true)}>
            <label htmlFor="name">Name</label>
            <input type="text" id="name" name="name" required />
            <label htmlFor="email">Email</label>
            <input type="email" id="email" name="email" required />
            <label htmlFor="message">Message</label>
            <textarea id="message" name="message" rows="5" required></textarea>
            <button type="submit" className="contact-submit">Submit</button>
          </form>
        )}
      </div>
    </motion.section>
  );
}

// Animation variant for fade and slide effect
const fadeSlide = {
  hidden: { opacity: 0, y: 40 },
  visible: { opacity: 1, y: 0, transition: { duration: 0.8 } }
};

// Card animation for projects
const cardVariants = {
  hidden: { opacity: 0, y: 40 },
  visible: { opacity: 1, y: 0, transition: { duration: 0.7 } },
  hover: { scale: 1.03 }
};

// Example tech stack data
const techStack = [
  { name: 'React', icon: <FaReact color="#61dafb" /> },
  { name: 'JavaScript', icon: <FaJs color="#f7df1e" /> },
  { name: 'CSS3', icon: <FaCss3Alt color="#2965f1" /> },
  { name: 'HTML5', icon: <FaHtml5 color="#e34c26" /> },
];

// Real GitHub projects data
const projects = [
  {
    title: 'educonnect-app',
    description: 'A web application for educational connectivity and collaboration.',
    image: 'https://images.unsplash.com/photo-1519389950473-47ba0277781c?auto=format&fit=crop&w=600&q=80',
    tech: [<FaJs />],
    live: '',
    github: 'https://github.com/ramkumar122/educonnect-app',
  },
  {
    title: 'fingerprint-voting-system',
    description: 'A Python-based fingerprint voting system for secure elections.',
    image: 'https://images.unsplash.com/photo-1519125323398-675f0ddb6308?auto=format&fit=crop&w=600&q=80',
    tech: [<FaJs />],
    live: '',
    github: 'https://github.com/ramkumar122/fingerprint-voting-system',
  },
  {
    title: 'my-s3-upload-project',
    description: 'A project for uploading files to AWS S3 using HCL.',
    image: 'https://images.unsplash.com/photo-1461749280684-dccba630e2f6?auto=format&fit=crop&w=600&q=80',
    tech: [<FaCloud />],
    live: '',
    github: 'https://github.com/ramkumar122/my-s3-upload-project',
  },
  {
    title: '2048-game',
    description: 'Its a web 2048 game.',
    image: 'https://images.unsplash.com/photo-1506744038136-46273834b3fb?auto=format&fit=crop&w=600&q=80',
    tech: [<FaJs />],
    live: '',
    github: 'https://github.com/ramkumar122/2048-game',
  },
  {
    title: 'blood-bank',
    description: 'A blood bank management system.',
    image: 'https://images.unsplash.com/photo-1515168833906-d2a3b82b302b?auto=format&fit=crop&w=600&q=80',
    tech: [<FaJs />],
    live: '',
    github: 'https://github.com/ramkumar122/blood-bank',
  },
];

// Example testimonials data
const testimonials = [
  {
    quote: 'Alex delivered a fantastic website, on time and beyond expectations!',
    image: 'https://randomuser.me/api/portraits/women/44.jpg',
    name: 'Jane Doe',
    title: 'Product Manager, Acme Inc.'
  },
  {
    quote: 'Great communication and technical skills. Highly recommended!',
    image: 'https://randomuser.me/api/portraits/men/32.jpg',
    name: 'John Smith',
    title: 'CTO, BetaTech'
  }
];

// Example experience data
const experience = [
  {
    role: 'Amazon SDE Intern',
    company: 'Amazon',
    period: 'May–Aug 2025',
    description: 'Built AWS CDK constructs and designed automation (see resume for details).'
  },
  {
    role: 'UI/UX Coordinator',
    company: 'ASU',
    period: '2024 - 2025',
    description: 'Coordinated UI/UX for student projects and led design sprints.'
  }
];

const Home = () => {
  return (
    <div className="portfolio-root">
      {/* Home Section (Redesigned Hero) */}
      <motion.section id="home" className="home-section" initial="hidden" animate="visible" variants={fadeSlide}>
        <div className="home-center">
          {/* No photo for minimal hero, as requested */}
          <motion.h1 
            className="home-title"
            initial={{ opacity: 0, y: 40 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
          >
            Ramkumar Meenavalli
          </motion.h1>
          <motion.p 
            className="home-subtitle"
            initial={{ opacity: 0, y: 40 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.2, duration: 0.8 }}
          >
            Software Developer &amp; IT Graduate
          </motion.p>
          <motion.div className="home-socials" initial={{ opacity: 0, y: 20 }} animate={{ opacity: 1, y: 0 }} transition={{ delay: 0.4, duration: 0.7 }}>
            <a href="mailto:rammeenavalli0@gmail.com" className="home-social-link"><FaEnvelope /></a>
            <a href="https://www.linkedin.com/in/ramkumar-meenavalli" target="_blank" rel="noopener noreferrer" className="home-social-link"><FaLinkedin /></a>
            <a href="https://github.com/ramkumar122" target="_blank" rel="noopener noreferrer" className="home-social-link"><FaGithub /></a>
          </motion.div>
          <motion.a 
            href="#projects" 
            className="home-cta"
            whileHover={{ scale: 1.06 }}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.6, duration: 0.7 }}
          >
            View My Work <FaExternalLinkAlt />
          </motion.a>
        </div>
      </motion.section>

      {/* About + Skills Section - benscott.dev inspired */}
      <section id="about" className="about-skills-section">
        <div className="about-skills-container">
          <motion.div className="about-col" initial="hidden" animate="visible" variants={fadeSlide}>
            <motion.h2 
              className="about-title"
              initial={{ opacity: 0, y: 30 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.7 }}
            >
              About Me
            </motion.h2>
            <motion.p
              className="about-desc"
              initial={{ opacity: 0, y: 30 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.2, duration: 0.8 }}
            >
              I am a results-driven software developer and IT graduate student at Arizona State University, passionate about building impactful digital solutions. My experience as an SDE Intern at Amazon includes designing AWS CDK constructs, automating certificate rotation, and enhancing system security. I have a strong foundation in Python, Java, SQL, and TypeScript, and enjoy leveraging machine learning and data visualization to solve real-world problems. I thrive in collaborative environments, value creative problem-solving, and am committed to continuous learning and innovation.
            </motion.p>
          </motion.div>
          <motion.div className="skills-col" initial="hidden" animate="visible" variants={fadeSlide}>
            <div className="skills-list-verticalgrid-3cols">
              {/* 3 columns, 4 skills per column */}
              {[
                [
                  { name: 'Python', icon: <FaJs color="#3776AB" size={28} /> },
                  { name: 'TensorFlow', icon: <FaJs color="#ff6f00" size={28} /> },
                  { name: 'TypeScript', icon: <FaJs color="#3178c6" size={28} /> },
                  { name: 'HTML5', icon: <FaHtml5 color="#e34c26" size={28} /> },
                ],
                [
                  { name: 'Java', icon: <FaJs color="#f89820" size={28} /> },
                  { name: 'Matplotlib', icon: <FaJs color="#11557c" size={28} /> },
                  { name: 'Bokeh', icon: <FaJs color="#e76f51" size={28} /> },
                  { name: 'CSS3', icon: <FaCss3Alt color="#2965f1" size={28} /> },
                ],
                [
                  { name: 'SQL', icon: <FaJs color="#00618a" size={28} /> },
                  { name: 'Seaborn', icon: <FaJs color="#43b7ba" size={28} /> },
                  { name: 'AWS', icon: <FaCloud color="#ff9900" size={28} /> },
                  { name: 'React', icon: <FaReact color="#61dafb" size={28} /> },
                ],
              ].map((col, colIdx) => (
                <div className="skills-list-col" key={colIdx}>
                  {col.map((tech, i) => (
                    <motion.div
                      className="skills__item"
                      key={tech.name}
                      initial={{ opacity: 0, y: 40 }}
                      animate={{ opacity: 1, y: 0 }}
                      transition={{ delay: 0.2 + (colIdx * 4 + i) * 0.08, duration: 0.6 }}
                    >
                      <span className="skills__icon">{tech.icon}</span>
                      <span className="skills__label">{tech.name}</span>
                    </motion.div>
                  ))}
                </div>
              ))}
            </div>
          </motion.div>
        </div>
      </section>

      {/* Projects Section - Advanced Animated */}
  <motion.section id="projects" className="projects-section" initial="hidden" animate="visible" variants={fadeSlide}>
        <h2 className="projects-title">Projects</h2>
        <div className="projects-advanced-grid">
          {projects.map((project, idx) => (
            <motion.div
              className="project-advanced-card"
              key={project.title}
              initial={{ opacity: 0, y: 40, scale: 0.95 }}
              animate={{ opacity: 1, y: 0, scale: 1 }}
              whileHover={{ scale: 1.04, boxShadow: '0px 8px 32px 0px rgba(167,139,250,0.18)' }}
              transition={{ duration: 0.5, type: 'spring' }}
            >
              <div className="project-advanced-image-wrap">
                <img
                  src={project.image || 'https://images.unsplash.com/photo-1461749280684-dccba630e2f6?auto=format&fit=crop&w=600&q=80'}
                  alt={project.title}
                  className="project-advanced-image"
                  onError={e => { e.target.onerror = null; e.target.src = 'https://images.unsplash.com/photo-1461749280684-dccba630e2f6?auto=format&fit=crop&w=600&q=80'; }}
                />
              </div>
              <div className="project-advanced-info">
                <h3 className="project-title">{project.title}</h3>
                <p className="project-desc">{project.description}</p>
                <div className="project-tech">
                  {project.tech.map((icon, i) => (
                    <span key={i} className="project-tech-icon">{icon}</span>
                  ))}
                </div>
                <div className="project-links">
                  {project.live && (
                    <a href={project.live} target="_blank" rel="noopener noreferrer" className="project-live-link">
                      Live <FaExternalLinkAlt />
                    </a>
                  )}
                  <a href={project.github} target="_blank" rel="noopener noreferrer" className="project-github-link">
                    GitHub <FaGithub />
                  </a>
                </div>
              </div>
            </motion.div>
          ))}
        </div>
      </motion.section>

      {/* Testimonials Section */}
      <motion.section id="testimonials" className="testimonials-section" initial="hidden" whileInView="visible" viewport={{ once: true }} variants={fadeSlide}>
        <h2 className="testimonials-title">Kind words from satisfied clients</h2>
        <div className="testimonials-list">
          {testimonials.map((t, i) => (
            <motion.div key={i} className="testimonial-card" initial={{ opacity: 0, y: 40 }} whileInView={{ opacity: 1, y: 0 }} viewport={{ once: true }} transition={{ duration: 0.7 }}>
              <FaQuoteLeft className="testimonial-quote-icon" />
              <p className="testimonial-quote">{t.quote}</p>
              <img src={t.image} alt={t.name} className="testimonial-photo" />
              <div className="testimonial-name">{t.name}</div>
              <div className="testimonial-title">{t.title}</div>
            </motion.div>
          ))}
        </div>
      </motion.section>

      {/* Experience Section */}
      <motion.section id="experience" className="experience-section" initial="hidden" whileInView="visible" viewport={{ once: true }} variants={fadeSlide}>
        <h2 className="experience-title">My Work Experience</h2>
        <div className="experience-list">
          {experience.map((exp, i) => (
            <motion.div key={i} className="experience-card" initial={{ opacity: 0, y: 40 }} whileInView={{ opacity: 1, y: 0 }} viewport={{ once: true }} transition={{ duration: 0.7 }}>
